  #{helpTime,jdbcType=DATE}, #{activityId,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}
  )

mapper.update:
update activity_help
set help_id = #{toCustomerId,jdbcType=INTEGER}
