mapper.select:
select 

from activity_prize

prize_amount > 0

    and issue_object = #{issueObject,jdbcType=INTEGER}


    and type = #{type,jdbcType=INTEGER}


    and is_fokas = #{isFokas,jdbcType=INTEGER}


    and issue_object = #{issueObject,jdbcType=INTEGER}

mapper.insert:
insert into activity_prize (id, prize_name, issue_object, 
  issue_rules, prize_weight, prize_amount, 
  type, is_fokas, picture_path,prize_remark
  )
values (#{id,jdbcType=INTEGER}, #{prizeName,jdbcType=VARCHAR}, #{issueObject,jdbcType=INTEGER}, 
  #{issueRules,jdbcType=VARCHAR}, #{prizeWeight,jdbcType=DOUBLE}, #{prizeAmount,jdbcType=INTEGER}, 
  #{type,jdbcType=INTEGER}, #{isFokas,jdbcType=INTEGER}, #{picturePath,jdbcType=VARCHAR}, #{prizeRemark,jdbcType=VARCHAR}
  )
